codec = RouterCodec()

encoded_input = (
    codec.encode.chain()
    .wrap_eth(FunctionRecipient.ROUTER, amount_in)
    .v2_swap_exact_in(FunctionRecipient.SENDER, amount_in, min_amount_out, path, payer_is_sender=False)
    .build(codec.get_default_deadline())
)

